sp_widget.client_script:
api.controller=function() {
  /* widget controller */
 		var c = this;

	//Listをクリックしたとき
		c.selectItem = function(idx) {
		var index = idx;
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID:' + id);
		$rootScope.noteID = id;
		//Notes Bodyを呼び出す
		var event = $rootScope.$emit('selectNote', id);
		console.log('$emitの戻り値', event);
	}

	//Notes Bodyで内容が更新されたとき
	$rootScope.$on('updateNote', function(event, data) {
		console.log('更新後ListのCS');
		//Listを更新する
		c.server.refresh();
	});
};

sp_widget.script:
(function() {
	data.notes = [];
	//テーブル検索
	//ログインユーザのレコードを作成日時の降順で取得
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	//上記の取得件数分、dataオブジェクトにpush
	while(noteGR.next()){
		var noteObj = {};
		//noteObjに取得したnoteGRの「number,title,sys_id」を追加
		//$sp.getRecordDisplayValues:指定されたフィールドの表示値をデータパラメータにコピー
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		//noteObj.noteに取得したnoteGRのnoteを先頭から20文字追加
		noteObj.note = noteGR.getValue('note').slice(0, 20);
		data.notes.push(noteObj);
	}
})();
